body {
  font-size: 1.6rem;
  font-family: "Oswald", sans-serif;
}

.wrapper {
  max-width: 114rem;
  margin: 0 auto;
  padding: 0 1rem;
}

.hamburger {
  display: inline-block;
  cursor: pointer;
  background-color: transparent;
  border: 0;
  margin: 0;
  margin-left: auto;
}

@media (min-width: 800px) {
  .hamburger {
    display: none;
  }
}
.hamburger,
.nav {
  transition: transform 0.3s 0.1s ease-in-out;
}

.hamburger__box {
  width: 35px;
  height: 24px;
  display: inline-block;
  position: relative;
}

.hamburger__inner {
  width: 100%;
  height: 3px;
  background-color: #000;
  position: absolute;
  left: 0;
  top: 50%;
  transform: translateY(-50%);
  transition: background-color 0.1s 0.2s ease-in-out;
}

.hamburger__inner::before,
.hamburger__inner::after {
  width: 100%;
  height: 3px;
  background-color: #000;
  position: absolute;
  content: "";
  left: 0;
  transition: transform 0.2s 0.2s ease-in-out;
}

.hamburger__inner::before {
  top: -10px;
}

.hamburger__inner::after {
  top: 10px;
}

.hamburger--active .hamburger__inner {
  background-color: transparent;
}

.hamburger--active .hamburger__inner:before {
  transform: translateY(10px) rotate(45deg);
}

.hamburger--active .hamburger__inner:after {
  transform: translateY(-10px) rotate(-45deg);
}

.logo {
  height: 45px;
  width: 130px;
}

.logo img {
  height: 100%;
  width: 100%;
  display: block;
}

.header .wrapper {
  display: flex;
  width: 100%;
  height: 100%;
  padding: 0.5rem;
}

.header {
  position: fixed;
  display: flex;
  top: 0;
  left: 0;
  right: 0;
  background: #fff;
}

.nav {
  height: 100vh;
  width: 250px;
  background-color: #fff;
  position: absolute;
  top: 0;
  right: 0;
  transform: translateX(250px);
  border-left: 1px solid #ccc;
  display: flex;
  align-items: center;
  justify-content: center;
}

.nav--active {
  transform: translateX(0px);
}

.nav__menu {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.nav__list {
  list-style: none;
  margin: 1rem 0;
}

.nav__link {
  color: #000;
  text-decoration: none;
  text-transform: uppercase;
}

@media (min-width: 800px) {
  .nav {
    position: sticky;
    height: auto;
    width: auto;
    border: none;
    margin-left: auto;
    transform: translateX(0);
  }

  .nav__menu {
    flex-direction: row;
    padding-top: 0rem;
  }

  .header .wrapper {
    display: flex;
  }

  .header {
    padding: 1rem 0;
  }

  .nav__link {
    display: block;
    padding: 0.5rem;
    margin-left: 2rem;
  }

  .nav__list {
    margin: 0;
  }
}
.top-bar {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  background: #fff;
  height: 30px;
  display: flex;
  justify-content: center;
  align-items: center;
  display: none;
}

.top-bar__icon-box {
  display: flex;
  justify-content: center;
  align-items: center;
}

.top-bar__icon {
  height: 2rem;
}

.top-bar__content {
  margin-left: 1rem;
}

.hero {
  background-image: url(/ff641709c705d23e398d569a2d8c5294.png);
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  min-height: 60vh;
  padding: 10rem 0;
}

.hero__content {
  color: #fff;
  text-transform: uppercase;
  letter-spacing: 3px;
}

.hero__title {
  font-size: 4rem;
  font-weight: 700;
}
.hero__title span {
  font-size: 2rem;
  display: block;
  font-weight: 400;
}

.hero__subtitle {
  font-size: 1.6rem;
  font-weight: 400;
  color: #838383;
}

.hero__cta {
  text-decoration: none;
  color: #fff;
  font-weight: 400;
  border: 2px solid #58a6ff;
  padding: 1rem 4rem;
  border-radius: 8px;
  display: inline-block;
  margin-top: 3.5rem;
  font-size: 1.6rem;
}

.overlay {
  height: 100%;
  width: 100%;
  background: #000;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  -webkit-box-sizing: border-box;
}

html {
  font-size: 62.5%;
}

body {
  height: 300vh;
}

